import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg'
import { Link, useNavigate } from 'react-router-dom'

const NotePage = () => {

    let params = useParams()
    let navigate = useNavigate()
    let id = params.id //ID from the URL
    let [note, setNote] = useState(null)

    const getNote = async () => {
        let response = await fetch(`/api/note/${id}`);
        let data = await response.json()
        setNote(data)
    }

    useEffect(() => {
        getNote()
    }, [setNote]);

    let updateNote = async () => {
        fetch(`/api/note/${id}/update`, {
            method: "PUT",
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(note)
        })
    }

    let deleteNote = async () => {
        fetch(`/api/note/${id}/delete`, {
            method: "DELETe",
            headers: {
                'Content-Type': 'application/json'
            }
        })
        navigate('/')
    }

    let handleSubmit = () => {
        updateNote()
        navigate('/')
    }

    return (
        <div className='note'>
            <div className='note-header'>
                <h3 onClick={handleSubmit}> <ArrowLeft /> </h3>
                {id !== 'new' ? (
                    <button onClick={deleteNote}>Delete</button>
                ) : (
                    <button>Done</button>
                )}

            </div>
            <textarea defaultValue={note?.body} onChange={(e) => { setNote({ ...note, 'body': e.target.value }) }}></textarea>
        </div>
    )
}

export default NotePage
