import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import { ReactComponent as ArrowLeft } from '../assets/arrow-left.svg'
import { Link } from 'react-router-dom'

const NotePage = () => {

    let params = useParams()
    let id = params.id //ID from the URL
    let [note, setNote] = useState(null)

    const getNote = async () => {
        let response = await fetch(`/api/note/${id}`);
        let data = await response.json()
        setNote(data)
    }

    useEffect(() => {
        getNote()
    });

    let updateNote = async () => {
        let response = await fetch(`/api/note/${id}/update`,
            {
                method: 'PUT',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(note)
            })
    }

    let handleSubmit = () => {
        updateNote()
    }

    return (
        <div className='note'>
            <div className='note-header'>
                <h3> <Link to='/'> <ArrowLeft /> </Link> </h3>
            </div>
            <textarea defaultValue={note?.body} onChange={(e) => { setNote({ ...note, 'body': e.target.value }) }}></textarea>
        </div>
    )
}

export default NotePage
